import Prism from "prismjs";
import "prismjs/components/prism-go.min.js";
import "prismjs/components/prism-rust.min.js";
import "prismjs/components/prism-markdown.min.js";
import "prismjs/components/prism-bash.min.js";
import "prismjs/components/prism-json.min.js";
import "prismjs/components/prism-protobuf.min.js";
import "prismjs/components/prism-solidity.min.js";
import copy from "clipboard-copy";
import { Base64 } from "js-base64";

export default {
  props: {
    /**
     * Code rendered in the body of the block
     */
    value: {
      type: String,
    },
    /**
     * Code rendered in the body of the block in base64
     */
    base64: {
      type: String,
    },
    /**
     * URL for "View source" link and filename in the code-block's footer
     */
    url: {
      type: String,
    },
    /**
     * Language for syntax highlighting
     */
    language: {
      type: String,
    },
  },
  data: function() {
    return {
      expanded: null,
      maxHeight: null,
      copied: null,
      height: null,
      isExpandable: null,
    };
  },
  computed: {
    source() {
      if (this.base64) return Base64.decode(this.base64);
      return this.value;
    },
    out() {
      return this.$slots.default;
    },
  },
  mounted() {
    if (this.$refs.body) {
      this.isExpandable = this.$refs.body.scrollHeight > 1000;
      this.height = this.$refs.body.scrollHeight - 700;
      this.expanded = this.$refs.body.scrollHeight - 700 < 300;
      this.maxHeight = this.$refs.body.scrollHeight + "px";
    }
  },
  methods: {
    filename(url) {
      const tokens = url
        .replace(/\#.*$/, "")
        .split("/")
        .slice(7);
      if (tokens.length > 4) {
        return [
          tokens[0],
          tokens[1],
          "...",
          tokens.slice(-2)[0],
          tokens.slice(-2)[1],
        ].join(" / ");
      } else {
        return tokens.join(" / ");
      }
    },
    copy(value) {
      const val = value
        .replace(/&quot;/g, '"')
        .replace(/&lt;/g, "<")
        .replace(/&gt;/g, ">")
        .replace(/&amp;/g, "&");
      this.copied = true;
      copy(val);
      setTimeout(() => {
        this.copied = false;
      }, 2000);
    },
    highlighted(source) {
      const supportedSyntax = Prism.languages[this.language];
      if (supportedSyntax) {
        return Prism.highlight(
          source
            .replace(/&quot;/g, '"')
            .replace(/&lt;/g, "<")
            .replace(/&gt;/g, ">")
            .replace(/&amp;/g, "&"),
          supportedSyntax
        );
      } else {
        return source;
      }
    },
    expand(bool, scroll) {
      const container = this.$refs.container;
      this.expanded = bool;
      if (!bool && container && scroll) container.scrollIntoView();
    },
  },
};
