export default {
  name: "precommits-bar",
  computed: {
    progressStyle() {
      let barWidth = 0
      if (this.precommits) {
        barWidth = Math.round(this.precommits * 1000) / 10
      }
      return {
        width: barWidth + "%"
      }
    }
  },
  props: ["precommits"]
}
