import Headroom from "headroom.js"
import { mapGetters } from "vuex"
import disableScroll from "disable-scroll"
export default {
  name: "app-header",
  computed: {
    ...mapGetters(["links"])
  },
  data: () => ({
    activeMenuApp: false,
    desktop: false,
    headroom: null
  }),
  methods: {
    close() {
      this.activeMenuApp = false
      this.activeMenuFundraiser = false
      disableScroll.off()
    },
    goto(route) {
      this.close()
      this.$router.push(route)
    },
    toggleMenuApp() {
      this.activeMenuApp = !this.activeMenuApp
      if (this.activeMenuApp) disableScroll.on()
      else disableScroll.off()
    },
    toggleMenuFundraiser() {
      this.activeMenuFundraiser = !this.activeMenuFundraiser
      if (this.activeMenuFundraiser) disableScroll.on()
      else disableScroll.off()
    },
    watchWindowSize() {
      let w = Math.max(
        document.documentElement.clientWidth,
        window.innerWidth || 0
      )
      if (w >= 1152) {
        this.close()
        this.desktop = true
      } else {
        this.desktop = false
      }
    },
    disableHeadroom() {
      if (this.headroom) {
        this.headroom.destroy()
        this.headroom = null
      }
    },
    enableHeadroom() {
      if (!this.headroom) {
        this.headroom = new Headroom(this.$el)
        this.headroom.init()
      }
    }
  },
  mounted() {
    this.watchWindowSize()
    window.onresize = this.watchWindowSize
  },
  watch: {
    $route(to) {
      if (to.name === "index") {
        this.disableHeadroom()
      } else if (to.name === "ibc") {
        this.disableHeadroom()
      } else {
        this.enableHeadroom()
      }
    }
  }
}
