export default {
  name: "tm-btn",
  computed: {
    btnClass() {
      let value = "tm-btn__container"
      if (this.iconPos) value += ` tm-btn__icon-${this.iconPos}`
      if (this.size) value += ` tm-btn--size-${this.size}`
      if (this.theme) value += ` tm-btn--theme-${this.theme}`
      if (this.color) value += ` tm-btn--${this.color}`
      return value
    }
  },
  props: [
    "value",
    "icon",
    "icon-pos",
    "img",
    "type",
    "size",
    "theme",
    "to",
    "color"
  ]
}
