import TmCardTags from "./TmCardTags"
export default {
  name: "tm-card-hz",
  components: {
    TmCardTags
  },
  computed: {
    imgBgStyle() {
      if (this.imgBgSrc) {
        return {
          background: `url(${this.imgBgSrc}) center center no-repeat`,
          backgroundSize: "cover"
        }
      }
      return {}
    },
    cssClass() {
      let value = ""
      if (this.ended) {
        value += " tm-card-hz--ended"
      }
      if (this.flush) {
        value += " tm-card-hz--flush"
      }
      if (this.icon) {
        value += " tm-card-hz--icon"
      }
      if (this.landscape) {
        value += " tm-card-hz--landscape"
      }
      if (this.vertical) {
        value += " tm-card-hz--vertical"
      }
      if (this.lazy) {
        value += " tm-card-hz--lazy"
      }
      if (this.theme === "dark") {
        value += " tm-card-hz--dark"
      }
      return value
    },
    iconSrc() {
      return require(`../../assets/brands/color/${this.icon}.svg`)
    }
  },
  props: [
    "ended",
    "flush",
    "href",
    "icon",
    "icon-type",
    "img-src",
    "img-bg-src",
    "landscape",
    "vertical",
    "lazy",
    "title",
    "subtitle",
    "description",
    "author",
    "date",
    "tags",
    "to",
    "type",
    "theme",
    "newsletters"
  ]
}
