import { mapGetters } from "vuex"
import CardLogo from "cards/CardLogo"
import TmSection from "common/TmSection"
export default {
  name: "section-logos",
  components: {
    CardLogo,
    TmSection
  },
  computed: {
    ...mapGetters(["media"]),
    pinnedMedia() {
      if (this.media) {
        return this.media.filter(i => i.pinned)
      }
      return []
    }
  },
  mounted() {
    if (this.media.length === 0) {
      this.$store.dispatch("initializeMentions")
    }
  }
}
