export default {
  name: "tm-section",
  computed: {
    cssClass() {
      let value = ""
      if (this.theme === "dark") {
        value += " tm-section--dark"
      }
      if (this.theme === "stars") {
        value += " tm-section--stars"
      }
      if (this.layout === "hero") {
        value += " tm-section--hero tm-section--split"
      }
      if (this.layout === "split") {
        value += " tm-section--split"
      }
      if (this.layout === "center") {
        value += " tm-section--center"
      }
      return value
    }
  },
  props: [
    "bg",
    "suptitle",
    "title",
    "subtitle",
    "theme",
    "layout",
    "overlay",
    "project-title",
    "goz-wordmark",
    "stargate-title"
  ]
}
