import MarkdownIt from "markdown-it"
import { mapGetters } from "vuex"
export default {
  name: "modal-definition",
  computed: {
    ...mapGetters(["modals"]),
    def() {
      return this.modals.definition
    },
    css() {
      return {
        top: this.def.top + 10 + "px",
        left: this.def.left - 16 + "px",
        right: "auto",
        bottom: "auto"
      }
    }
  },
  data: () => ({
    md: new MarkdownIt()
  }),
  methods: {
    close() {
      this.$store.commit("closeDefinition")
    }
  }
}
